import React, { useEffect, useState } from 'react'
import "./CommentSection.css"
import Profile from "../../img/profileImg.jpg"
import { useDispatch, useSelector } from 'react-redux'
import { getComment } from '../../Action/CommentAction'



const CommentSection = ({ postId }) => {
    const dispatch = useDispatch()

    let { comments, loading, error } = useSelector((state) => state.commentReducer)
    useEffect(() => {
        dispatch(getComment(postId))
    }, [dispatch, postId])

    console.log(comments.length);
    // if (loading) {
    //     return <div>Loading comments...</div>;
    //   }

    //   if (error) {
    //     return <div>Error: {error}</div>;
    //   }
    const [count, setCount] = useState(0);

 
let commentSection = comments.length <= 2 ? (<div>
    {console.log(comments)}
</div>): comments?.map((comment) => {
    return (
        <div className='commenter'>
            <div className='firstchild'>
                {/* <img src={Profile} alt="" className='commentImage' /> */}
                <div className="name">
                </div>
            </div>
            <div className='secondchild'>
                <span>Mohammed Nihal</span>
                <p>{comment?.comment}</p>
                {/* Lorem ipsum, dolor sit amet consectetur adipisicing elit. Voluptatem ad ipsum suscipit incidunt sed ullam labore culpa minus error amet, recusandae eos, velit laborum eaque, tempore reprehenderit iure aspernatur dolore? */}
            </div>


        </div>
    )

})
useEffect(() => {
    let counter = count;
    const interval = setInterval(() => {
      if (counter >= commentSection.length) {
        clearInterval(interval);
      } else {
        setCount(count => count + 1);
        counter++; // local variable that this closure will see
      }
    }, 500);
    return () => clearInterval(interval); 
  }, [commentSection]);

    return (
        <>
            {commentSection}



        </>

    )
}

export default CommentSection
