const commentReducer = (
    state = { comments: [null], loading: false, error: false },
    action
  ) => {
    switch (action.type) {
      // belongs to commentshare.jsx
    
      // belongs to comments.jsx
      case "COMMENT_START":
        return { ...state, loading: true, error: false };
      case "COMMENT_SUCCESS":
        return { ...state, comments: [action.data, ...state.posts].data, loading: false, error: false };
      case "COMMENT_FAIL":
        return { ...state, loading: false, error: true };
      default:
        return state;
    }
  };
  
  export default commentReducer;
